sys_variable_value.value:
var grAH = new GlideRecord('alm_hardware');
if (grAH.get(current.variables.asset.toString())) {
    grAH.install_status = 1; // check if asset state is inuse
    grAH.substatus = '';
    grAH.assigned_to = current.opened_by.toString();
	grAH.checked_out=current.variables.check_out_date.toString();
    grAH.update();
}

